document.addEventListener("DOMContentLoaded",function(){if(localStorage.getItem("theme")==="dark"||(!localStorage.getItem("theme")&&window.matchMedia("(prefers-color-scheme: dark)").matches)){document.documentElement.classList.add("dark")}});document.addEventListener("DOMContentLoaded",function(){var themeConfig={theme:"light","theme-base":"gray","theme-font":"sans-serif","theme-primary":"blue","theme-radius":"1"};var url=new URL(window.location);var form=document.getElementById("offcanvasSettings");var resetButton=document.getElementById("reset-changes");var updateThemeMode=function(mode){if(mode==="dark"){document.body.classList.add("theme-dark");document.documentElement.setAttribute("data-bs-theme","dark")}else{document.body.classList.remove("theme-dark");document.documentElement.setAttribute("data-bs-theme","light")}};var updateFontFamily=function(font){const fontMap={"sans-serif":"var(--tblr-font-sans-serif)","serif":"var(--tblr-font-serif)","monospace":"var(--tblr-font-monospace)","comic":"var(--tblr-font-comic)"};document.documentElement.style.setProperty("--tblr-font-family",fontMap[font]||fontMap["sans-serif"]);document.body.style.fontFamily=fontMap[font]||fontMap["sans-serif"]};var updateCornerRadius=function(radius){document.documentElement.style.setProperty("--tblr-border-radius",`${radius}rem`)};var updateGrayShade=function(base){document.documentElement.setAttribute("data-theme-base",base);document.body.classList.remove("theme-slate","theme-gray","theme-zinc","theme-neutral","theme-stone");document.body.classList.add(`theme-${base}`)};var checkItems=function(){for(var key in themeConfig){var value=localStorage.getItem("tabler-"+key)||themeConfig[key];if(value){var radios=form.querySelectorAll(`[name="${key}"]`);if(radios){radios.forEach(function(radio){radio.checked=radio.value===value;if(key==="theme-primary"){document.documentElement.style.setProperty("--tblr-primary",`var(--tblr-${value})`)}if(key==="theme"){updateThemeMode(value)}if(key==="theme-font"){updateFontFamily(value)}if(key==="theme-radius"){updateCornerRadius(value)}if(key==="theme-base"){updateGrayShade(value)}})}}}};form.addEventListener("change",function(event){var target=event.target;var name=target.name;var value=target.value;for(var key in themeConfig){if(name===key){document.documentElement.setAttribute("data-"+key,value);localStorage.setItem("tabler-"+key,value);url.searchParams.set(key,value);if(key==="theme-primary"){document.documentElement.style.setProperty("--tblr-primary",`var(--tblr-${value})`)}if(key==="theme"){updateThemeMode(value)}if(key==="theme-font"){updateFontFamily(value)}if(key==="theme-radius"){updateCornerRadius(value)}if(key==="theme-base"){updateGrayShade(value)}}}window.history.pushState({},"",url)});resetButton.addEventListener("click",function(){for(var key in themeConfig){var value=themeConfig[key];document.documentElement.removeAttribute("data-"+key);localStorage.removeItem("tabler-"+key);url.searchParams.delete(key);if(key==="theme-primary"){document.documentElement.style.setProperty("--tblr-primary",`var(--tblr-${value})`)}if(key==="theme"){updateThemeMode(value)}if(key==="theme-font"){updateFontFamily(value)}if(key==="theme-radius"){updateCornerRadius(value)}if(key==="theme-base"){updateGrayShade(value)}}checkItems();window.history.pushState({},"",url)});checkItems();for(const[key,value]of url.searchParams.entries()){if(themeConfig.hasOwnProperty(key)){localStorage.setItem("tabler-"+key,value)}}checkItems()});
